import React from "react";

import Layout from "../components/layout";

// A hero div
const Hero = () => (
    <div>
        <div style={{
            background: 'url("/static/images/bridge.png")',
            backgroundSize: 'cover',
            height: '50vh',
            backgroundPosition: 'center top',
        }}></div>
    </div>
)



class Index extends React.Component {
    constructor(props) {
        super(props);

        this.state = { daysUntil: Math.ceil(Math.abs((new Date() - new Date('2019-10-05'))/1000/60/60/24))}
    }
    render() {
        return (
            <Layout>
                <Hero />        

                <div style={{
                    height: '50vh',
                    textAlign: 'center',
                    paddingTop: '20px',
                }}>
                    <h1>Michelle Phelps &amp; Greg Potts</h1>
                    <h2>We're getting married!</h2>
                    <p style={{paddingTop: '10px'}}>
                        Join us on <em>Saturday, October 5th 2019</em> at&nbsp;
                        <a href="https://www.google.com/maps/place/Main+Street+United+Methodist+Church/@34.1794938,-82.3825782,17z/data=!3m1!4b1!4m5!3m4!1s0x88f7fb568c99204d:0x548dcae9dea4a05e!8m2!3d34.1794938!4d-82.3803895">
                            Main Street United Methodist Church, Abbeville
                        </a>
                        at 4:00pm sharp
                    </p>
                    <small>Only {this.state.daysUntil} days left!</small>
                </div>
            </Layout>
        );
    }
}

export default Index;
