import React from 'react';

import Layout from '../components/layout';

const EmmaImagePath = '/static/images/200x200.jpg';
const BrookeImagePath = '../static/images/200x200.jpg';
const KahliImagePath = '/static/images/200x200.jpg';
const KatherineImagePath = '../static/images/200x200.jpg';
const MadeleineImagePath = '../static/images/200x200.jpg';
const NatalieImagePath = '../static/images/200x200.jpg';
const PaigeImagePath = '../static/images/200x200.jpg';

const RyanImagePath = '../static/images/200x200.jpg';
const TylerImagePath = '../static/images/200x200.jpg';

const PartyMember = props => (
  <div
    style={{
      width: '400px',
      background: props.isMaidOfHonor ? '#6A2541' : '#F0EAD6',
      color: props.isMaidOfHonor ? 'white' : 'black',
      borderRadius: '5px',
      margin: '10px',
      padding: '20px',
    }}>
    <img
      src={props.img}
      style={{
        width: '200px',
        height: '200px',
        borderRadius: '2px',
        display: 'block',
        margin: 'auto',
        marginBottom: '20px',
      }}
    />
    <h4>
      {props.name}{' '}
      {props.isMaidOfHonor ? (
        <span style={{color: '#DAA520'}}>&mdash; Maid of Honor</span>
      ) : (
        ''
      )}
    </h4>
    <p>{props.blurb}</p>
  </div>
);

class MeetTheParty extends React.Component {
  state = {};

  render() {
    return (
      <Layout container="true">
        <h1>The Party</h1>
        <h2>Bridesmaids</h2>
        <div style={{display: 'flex', flexDirection: 'row'}}>
          <PartyMember
            name="Emma"
            isMaidOfHonor
            img={EmmaImagePath}
            blurb="Lorem"
          />
          <PartyMember name="Brooke" img={BrookeImagePath} blurb="Lorem" />
          <PartyMember
            name="Emma"
            isMaidOfHonor
            img={EmmaImagePath}
            blurb="Lorem"
          />
        </div>
        <hr />

        <h2>Groomsmen</h2>
      </Layout>
    );
  }
}

export default MeetTheParty;
