import React from 'react';

import Layout from '../components/layout';

const CardSection = props => (
<section style={{
margin: '20px 5px', 
textAlign: 'center'
}}>
<h1>Greg &amp; Michelle</h1>
<p style={{padding: '5px 0px'}}>
<em>Saturday, October 5th, 2019 at 4:00pm</em> &mdash; Abbeville, SC
</p>
<p style={{padding: '5px 0px'}}></p>
</section>
);

const Hero = () => (
  <div
    style={{
      background: 'url("/static/images/bridge.png")',
      backgroundSize: 'cover',
      height: '50vh',
      backgroundPosition: 'center top',
    }}
  />
);

const Index = () => (
  <Layout>
    <Hero />
    <CardSection />
  </Layout>
);

export default Index;
