import React from 'react';

import Layout from '../components/layout';

const Hero = () => (
  <div
    style={{
      background: 'url("/static/images/bridge.png")',
      backgroundSize: 'cover',
      height: 'calc(50vh - 76px)',
      backgroundPosition: 'center top',
    }}
  />
);

const CardSection = props => (
  <section
    style={{
      height: '49vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      textAlign: 'center',
      padding: '10px',
      background: 'grey',
    }}>
    <h1>Greg &amp; Michelle</h1>
    <p style={{padding: '5px 0px'}}>
      <em>Saturday, October 5th, 2019 at 4:00pm</em> &mdash; Abbeville, SC
    </p>
  </section>
);

const Index = () => (
  <Layout>
    <Hero />
    <CardSection />
  </Layout>
);

export default Index;
