import React from 'react';

import Layout from '../components/layout';

const Hero = () => (
  <div
    style={{
      background: 'url("/static/images/bridge.png")',
      backgroundSize: 'cover',
      height: 'calc(50vh - 76px)',
      backgroundPosition: 'center top',
    }}
  />
);

const CardSection = props => (
  <section
    style={{
      height: '49vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      textAlign: 'center',
      background: 'rgb(0,0,0)',
      background:
        'linear-gradient(0deg, rgba(0,0,0,1) 0%, rgba(106,37,65,1) 84%)',
    }}>
    <div
      style={{
        background: 'grey',
        color: 'white',
        padding: '40px',
        borderRadius: '3px',
      }}>
      <h1>Greg &amp; Michelle</h1>
      <p style={{padding: '5px 0px'}}>
        <em>Saturday, October 5th, 2019 at 4:00pm</em> &mdash; Abbeville, SC
      </p>
    </div>
  </section>
);

const Index = () => (
  <Layout>
    <Hero />
    <CardSection />
  </Layout>
);

export default Index;
