import React from 'react';

import Layout from '../components/layout';

const CardSection = props => (
  <div style={{margin: '20px 5px', textAlign: 'center'}}>
    <h1>Greg &amp; Michelle</h1>
    <p>
      Saturday, <strong>October 5th, 2019</strong> &mdash; Abbeville, SC
    </p>
  </div>
);

const Hero = () => (
  <div>
    <div
      style={{
        background: 'url("/static/images/bridge.png")',
        backgroundSize: 'cover',
        height: '50vh',
        backgroundPosition: 'center top',
      }}
    />
  </div>
);

class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      daysUntil: Math.ceil(
        Math.abs((new Date() - new Date('2019-10-05')) / 1000 / 60 / 60 / 24),
      ),
    };
  }
  render() {
    return (
      <Layout>
        <Hero />
        <CardSection daysUntil={this.state.daysUntil} />
      </Layout>
    );
  }
}

export default Index;
