import React from "react";

class Index extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            i: 0
        }
    }

    onButtonClick() {
        this.setState({i: this.state.i + 1});
    }

    render() {
        return (
            <div>
                <p>Hi from stateful component</p>
                <p>{this.state.i}</p>
                <button onClick={this.onButtonClick}>
                    Button
                </button>
            </div>
        );
    }
}

export default Index;
