import React from 'react';

import Layout from '../components/layout';

/*
const Hero = () => (
  <div
    style={{
      background: 'url("/static/images/bridge.png")',
      backgroundSize: 'cover',
      height: '90vh',
      backgroundPosition: 'center top',
      maxWidth: '1000px',
      margin: 'auto',
    }}
  />
);

const CardSection = props => (
  <section
    style={{
      height: '10vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      textAlign: 'center',
      background: 'rgb(248,248,248)',
    }}>
    <div
      style={{
        background: 'white',
        color: 'black',
        padding: '70px',
        borderRadius: '3px',
      }}>
      <h1>Greg &amp; Michelle</h1>
      <p style={{padding: '5px 0px'}}>
        <em>Saturday, October 5th, 2019 at 4:00pm</em>
      </p>
      <p style={{padding: '5px 0px'}}>
        <a
          target="_blank"
          href="https://www.google.com/maps/place/Main+Street+United+Methodist+Church/@34.1794938,-82.3825782,17z/data=!3m1!4b1!4m5!3m4!1s0x88f7fb568c99204d:0x548dcae9dea4a05e!8m2!3d34.1794938!4d-82.3803895">
          Main Street United Methodist Church, Abbeville, SC 29620
        </a>
      </p>
    </div>
  </section>
);
*/

const Hero = () => (
  <div
    style={{
      position: 'relative',
      width: '100%',
    }}>
    <div
      style={{
        background: 'url("/static/images/bridge.png")',
        backgroundSize: 'cover',
        height: 'calc(100vh - 76px)',
        backgroundPosition: 'center top',
      }}
    />
    <div
      style={{
        position: 'absolute',
        top: '40vh',
        left: '15%',
        width: '70%',
        margin: 'auto',
        padding: '20px',
        zIndex: '10',
        textAlign: 'center',
        background: 'rgba(255, 255, 255, 0.7)',
      }}>
      <h1>We're getting married!</h1>
      <p>Greg &amp; Michelle</p>
    </div>
  </div>
);

const Index = () => (
  <Layout>
    <Hero />
  </Layout>
);

export default Index;
