import React from 'react';

import Layout from '../components/layout';

const Hero = () => (
  <div
    style={{
      background: 'url("/static/images/bridge.png")',
      backgroundSize: 'cover',
      height: 'calc(50vh - 70px)',
      backgroundPosition: 'center top',
      margin: '20px',
      borderRadius: '3px',
      maxWidth: '850px',
      margin: '20px auto',
    }}
  />
);

const CardSection = props => (
  <section
    style={{
      height: '50vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      textAlign: 'center',
      background: 'rgb(248,248,248)',
    }}>
    <div
      style={{
        background: 'white',
        color: 'black',
        padding: '60px',
        borderRadius: '3px',
      }}>
      <h1>Greg &amp; Michelle</h1>
      <p style={{padding: '5px 0px'}}>
        <em>Saturday, October 5th, 2019 at 4:00pm</em> &mdash; Abbeville, SC
      </p>
    </div>
  </section>
);

const Index = () => (
  <Layout>
    <Hero />
    <CardSection />
  </Layout>
);

export default Index;
