import React from 'react';

import Layout from '../components/layout';

const PartyMember = props => (
  <div
    style={{
      width: '20vw',
      background: props.isBridesMaid ? '#6a2541' : '#F0EAD6',
      borderRadius: '5px',
      margin: '10px',
      padding: '20px',
    }}>
    <strong>{props.name}</strong>
    <img
      src={props.img}
      style={{width: '100%', height: '30px', borderRadius: '2px'}}
    />
    <p>{props.blurb}</p>
  </div>
);

class MeetTheParty extends React.Component {
  state = {};

  render() {
    return (
      <Layout container="true">
        <h1>The Party</h1>
        <h2>Bridesmaids</h2>
      </Layout>
    );
  }
}

export default MeetTheParty;
